<div class="row bs-thumbnails js-html-inspector" data-remove-target="p" data-replace-target="> div">
    <div class="col-sm-3 col-xs-6">
        <p><strong>.img-rounded</strong></p>
        <img class="img-rounded" src="data:image/svg+xml;base64," alt=".img-rounded">
    </div>
    <div class="col-sm-3 col-xs-6">
        <p><strong>.img-circle</strong></p>
        <img class="img-circle" src="data:image/svg+xml;base64," alt=".img-circle">
    </div>
    <div class="col-sm-3 col-xs-6">
        <p><strong>.img-thumbnail</strong></p>
        <img class="img-thumbnail" src="data:image/svg+xml;base64," alt=".img-thumbnail">
    </div>
    <div class="col-sm-3 col-xs-6">
        <p><strong>.img-responsive</strong></p>
        <img class="img-responsive" src="data:image/svg+xml;base64," alt=".img-responsive">
    </div>
</div>